const screens = ['recovery', 'bootloader', 'bootloaderMode']
const isScreenActive = (screen: ?ScreenTypes) => screens.includes(screen)

const Blue = ({ width = 200, error, wire, action, screen }: Props) => {
  const color = error ? '#EA2E49' : '#6490F1'

  return (
    <svg
      viewBox="0 0 159 337"
      width={width}
      height={(width * 337) / 159}
      style={{ overflow: 'visible' }}
    >
      <g fill="none" fillRule="evenodd">
        <SideButton />
        <Frame color={color} />
        <Screen color={color} />
        {error ? <Cross /> : null}
        {wire === 'wired' ? <CableConnected /> : null}
        {wire === 'disconnecting' ? <CableDisconnecting /> : null}
        {wire === 'connecting' ? <CableConnecting /> : null}
        {action === 'left' ? <Hint /> : null}
        {action === 'accept' && !isScreenActive(screen) ? <ConfirmScreen /> : null}
        {screen === 'validation' ? <ConfirmScreen /> : null}
        {screen === 'recovery' ? <RecoveryScreen /> : null}
        {screen === 'bootloader' ? <BootloaderScreen /> : null}
        {screen === 'bootloaderMode' ? <BootloaderModeScreen /> : null}
        {screen === 'update' ? <UpdateScreen /> : null}
      </g>
    </svg>
  )
}

export default Blue
